import React, { useState } from "react";
// This will require to npm install axios
import axios from 'axios';

const Create = () => {
  const [stateObj = {}, objSet] = useState({ person_name: "", person_position: "", person_level: "" })

  console.log("The initial state is " + JSON.stringify(stateObj))
  // componentWillMount(){
  //   console.log("Componenet is mounting")
  // }

  // componentWillUnmount(){
  //   console.log("Componenet is unommounting")
  // }
  // componentDidMount(){
  //   console.log("Componenet mounted")
  // }
  // componentDidUpdate(){
  //   console.log("Componenet updated")
  // }

  // These methods will update the state properties.
  function onChangePersonName(e) {
    objSet({ person_name: e.target.value })
  }

  function onChangePersonPosition(e) {
    objSet({ person_position: e.target.value })
  }


  function onChangePersonLevel(e) {
    objSet({ person_level: e.target.value })
  }

  // This function will handle the submission.
  function onSubmit(e) {
    e.preventDefault();

    // When post request is sent to the create url, axios will add a new record(newperson) to the database.
    const newperson = {
      person_name: stateObj.person_name,
      person_position: stateObj.person_position,
      person_level: stateObj.person_level,
    };

    axios
      .post("http://localhost:3000/record/add", newperson)
      .then((res) => console.log(res.data));

    // We will empty the state after posting the data to the database
    objSet({
      person_name: "",
      person_position: "",
      person_level: "",
    });
  }


  // This following section will display the form that takes the input from the user.
  return (
    <div style={{ marginTop: 20 }}>
      <h3>Create New Record</h3>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label>Name of the person: </label>
          <input
            type="text"
            className="form-control"
            value={stateObj.person_name}
            onChange={onChangePersonName}
          />
        </div>
        <div className="form-group">
          <label>Person's position: </label>
          <input
            type="text"
            className="form-control"
            value={stateObj.person_position}
            onChange={onChangePersonPosition}
          />
        </div>
        <div className="form-group">
          <div className="form-check form-check-inline">
            <input
              className="form-check-input"
              type="radio"
              name="priorityOptions"
              id="priorityLow"
              value="Intern"
              checked={stateObj.person_level === "Intern"}
              onChange={onChangePersonLevel}
            />
            <label className="form-check-label">Intern</label>
          </div>
          <div className="form-check form-check-inline">
            <input
              className="form-check-input"
              type="radio"
              name="priorityOptions"
              id="priorityMedium"
              value="Junior"
              checked={stateObj.person_level === "Junior"}
              onChange={onChangePersonLevel}
            />
            <label className="form-check-label">Junior</label>
          </div>
          <div className="form-check form-check-inline">
            <input
              className="form-check-input"
              type="radio"
              name="priorityOptions"
              id="priorityHigh"
              value="Senior"
              checked={stateObj.person_level === "Senior"}
              onChange={onChangePersonLevel}
            />
            <label className="form-check-label">Senior</label>
          </div>
        </div>
        <div className="form-group">
          <input
            type="submit"
            value="Create person"
            className="btn btn-primary"
          />
        </div>
      </form>
    </div>
  );
}

export default Create
